--
-- Estructura de tabla para la tabla `noticia`
--

CREATE TABLE `noticia` (
  `noticia_id` int(11) NOT NULL,
  `noticia_orden` int(11) DEFAULT NULL,
  `noticia_titulo` varchar(100) DEFAULT NULL,
  `noticia_subtitulo` text,
  `noticia_descripcion` text NOT NULL,
  `noticia_descripcion2` text NOT NULL,
  `noticia_descripcion3` text,
  `noticia_imagen` varchar(100) NOT NULL,
  `noticia_imagen2` varchar(100) DEFAULT NULL,
  `noticia_imagen3` varchar(100) DEFAULT NULL,
  `noticia_link_face` varchar(250) DEFAULT NULL,
  `noticia_activo` int(11) DEFAULT NULL,
  `noticia_titulo_ingles` varchar(100) DEFAULT NULL,
  `noticia_subtitulo_ingles` text,
  `noticia_descripcion_ingles` text NOT NULL,
  `noticia_descripcion2_ingles` text NOT NULL,
  `noticia_descripcion3_ingles` text,
  `noticia_fecha` text NOT NULL
) ENGINE=MyISAM DEFAULT CHARSET=utf8;

--
-- Volcado de datos para la tabla `noticia`
--

INSERT INTO `noticia` (`noticia_id`, `noticia_orden`, `noticia_titulo`, `noticia_subtitulo`, `noticia_descripcion`, `noticia_descripcion2`, `noticia_descripcion3`, `noticia_imagen`, `noticia_imagen2`, `noticia_imagen3`, `noticia_link_face`, `noticia_activo`, `noticia_titulo_ingles`, `noticia_subtitulo_ingles`, `noticia_descripcion_ingles`, `noticia_descripcion2_ingles`, `noticia_descripcion3_ingles`, `noticia_fecha`) VALUES
(1, 1, 'Metropolitano: este es el horario especial por feriado', 'Servicio de transporte reducirá algunas rutas este viernes debido a la festividad de San Pedro y San Pablo. Número de agentes seguridad se mantendrá', '', '<p><span style=&quot;color: rgb(51, 51, 51); font-family: Georgia, &quot;Times New Roman&quot;, serif; font-size: 20px;&quot;>Este viernes 29 de junio el servicio del Metropolitano</span><b style=&quot;color: rgb(51, 51, 51); font-family: Georgia, &quot;Times New Roman&quot;, serif; font-size: 20px;&quot;> </b><span style=&quot;color: rgb(51, 51, 51); font-family: Georgia, &quot;Times New Roman&quot;, serif; font-size: 20px;&quot;>tendrá un cambio de horario debido a la festividad de San Pedro y San Pablo, que en varias zonas del país como Lima se desarrollará.</span><br></p>', '<p><span style=&quot;color: rgb(51, 51, 51); font-family: Georgia, &quot;Times New Roman&quot;, serif; font-size: 20px;&quot;>Al tratarse de un día feriado, Protransporte</span><span style=&quot;color: rgb(51, 51, 51); font-family: Georgia, &quot;Times New Roman&quot;, serif; font-size: 20px;&quot;>, informó que únicamente operarán los servicios regulares A, B y C desde las 5:00 a.m. hasta las 10:00 p.m., quedando suspendido el servicio regular D y los Expresos 1, 2, 3, 5, 6, 7, 8, 9, Súper Expreso y Súper Expreso Norte.</span><br></p>', '20180628123051.jpg', '201806281230512.jpg', '', '', 1, 'Metropolitano: este es el horario especial por feriado', 'Servicio de transporte reducirá algunas rutas este viernes debido a la festividad de San Pedro y San Pablo. Número de agentes seguridad se mantendrá', '', '<p><span style=&quot;color: rgb(51, 51, 51); font-family: Georgia, &quot;Times New Roman&quot;, serif; font-size: 20px;&quot;>Este viernes 29 de junio el servicio del Metropolitano</span><b style=&quot;color: rgb(51, 51, 51); font-family: Georgia, &quot;Times New Roman&quot;, serif; font-size: 20px;&quot;> </b><span style=&quot;color: rgb(51, 51, 51); font-family: Georgia, &quot;Times New Roman&quot;, serif; font-size: 20px;&quot;>tendrá un cambio de horario debido a la festividad de San Pedro y San Pablo, que en varias zonas del país como Lima se desarrollará.</span><br></p>', '<p><span style=&quot;color: rgb(51, 51, 51); font-family: Georgia, &quot;Times New Roman&quot;, serif; font-size: 20px;&quot;>Al tratarse de un día feriado, Protransporte</span><span style=&quot;color: rgb(51, 51, 51); font-family: Georgia, &quot;Times New Roman&quot;, serif; font-size: 20px;&quot;>, informó que únicamente operarán los servicios regulares A, B y C desde las 5:00 a.m. hasta las 10:00 p.m., quedando suspendido el servicio regular D y los Expresos 1, 2, 3, 5, 6, 7, 8, 9, Súper Expreso y Súper Expreso Norte.</span><br></p>', '27/ 06/ 2018'),
(2, 2, '#NoTePases| Coaster acumula multas por S/2,2 millones y sigue circulando', 'Vehículo registra más de 160 papeletas en solo tres años de transitar por Lima', '', '<p><span style=&quot;color: rgb(51, 51, 51); font-family: Georgia, &quot;Times New Roman&quot;, serif; font-size: 20px;&quot;>No hay sanción en el&nbsp;</span><b style=&quot;color: rgb(51, 51, 51); font-family: Georgia, &quot;Times New Roman&quot;, serif; font-size: 20px;&quot;>transporte público de Lima</b><span style=&quot;color: rgb(51, 51, 51); font-family: Georgia, &quot;Times New Roman&quot;, serif; font-size: 20px;&quot;>&nbsp;que ayude a cambiar la conducta de los choferes. Este nuevo caso de impunidad al volante revela por qué. Una deteriorada coaster marca Toyota de placa B1B-741 fue captada circulando libremente pese a que debe S/2,2 millones en papeletas, según el Servicio de Administración Tributaria (SAT).</span><br></p>', '<p class=&quot;parrafo first-parrafo&quot; id=&quot;p531012-m1365-1-1366&quot; style=&quot;font-size: 20px; line-height: 1.5; margin-bottom: 25px; word-wrap: break-word; color: rgb(51, 51, 51); font-family: Georgia, &quot;Times New Roman&quot;, serif;&quot;>También registra 21 órdenes de captura vigentes, pero no ha sido trasladada al depósito.</p><div id=&quot;ads_d_inline&quot; style=&quot;color: rgb(51, 51, 51); font-family: Georgia, &quot;Times New Roman&quot;, serif; font-size: 16px;&quot;></div><p class=&quot;parrafo first-parrafo&quot; id=&quot;p531012-m1375-2-1376&quot; style=&quot;font-size: 20px; line-height: 1.5; margin-bottom: 25px; word-wrap: break-word; color: rgb(51, 51, 51); font-family: Georgia, &quot;Times New Roman&quot;, serif;&quot;>Las papeletas que registra este bus, alrededor de 160, fueron emitidas en solo tres años. Es decir, ha generado más de S/60.000 al mes en infracciones. Según el portal de los Registros Públicos, el propietario es Jorge Sócrates Ruiz Ludeña, un hombre de 71 años que vive en Huancavelica. Sin embargo, más de cinco choferes distintos han conducido este vehículo, varios de ellos con un largo historial de multas.</p><p class=&quot;parrafo first-parrafo&quot; id=&quot;p531012-m1385-3-1386&quot; style=&quot;font-size: 20px; line-height: 1.5; margin-bottom: 25px; word-wrap: break-word; color: rgb(51, 51, 51); font-family: Georgia, &quot;Times New Roman&quot;, serif;&quot;>El portal del Ministerio de Transportes (MTC) indica que esta coaster no tiene revisión técnica vehicular vigente ni SOAT.</p><p class=&quot;parrafo first-parrafo&quot; id=&quot;p531012-m1390-4-1391&quot; style=&quot;font-size: 20px; line-height: 1.5; margin-bottom: 25px; word-wrap: break-word; color: rgb(51, 51, 51); font-family: Georgia, &quot;Times New Roman&quot;, serif;&quot;>Hace cerca de un mes,&nbsp;<b>El Comercio</b>&nbsp;reveló que la falta de espacio en los depósitos del SAT y de grúas municipales impiden que cientos de vehículos de transporte público puedan ser internados. La Gerencia de Transporte Urbano (GTU) de la Municipalidad de Lima sostiene que hay varios proyectos para ampliar la infraestructura de depósitos en la ciudad.&nbsp;</p>', '20180628123428.jpg', '201806281234282.jpg', '201806281234283.jpg', '', 1, '#NoTePases| Coaster acumula multas por S/2,2 millones y sigue circulando', 'Vehículo registra más de 160 papeletas en solo tres años de transitar por Lima', '', '<p><span style=&quot;color: rgb(51, 51, 51); font-family: Georgia, &quot;Times New Roman&quot;, serif; font-size: 20px;&quot;>No hay sanción en el&nbsp;</span><b style=&quot;color: rgb(51, 51, 51); font-family: Georgia, &quot;Times New Roman&quot;, serif; font-size: 20px;&quot;>transporte público de Lima</b><span style=&quot;color: rgb(51, 51, 51); font-family: Georgia, &quot;Times New Roman&quot;, serif; font-size: 20px;&quot;>&nbsp;que ayude a cambiar la conducta de los choferes. Este nuevo caso de impunidad al volante revela por qué. Una deteriorada coaster marca Toyota de placa B1B-741 fue captada circulando libremente pese a que debe S/2,2 millones en papeletas, según el Servicio de Administración Tributaria (SAT).</span><br></p>', '<p class=&quot;parrafo first-parrafo&quot; id=&quot;p531012-m1365-1-1366&quot; style=&quot;font-size: 20px; line-height: 1.5; margin-bottom: 25px; word-wrap: break-word; color: rgb(51, 51, 51); font-family: Georgia, &quot;Times New Roman&quot;, serif;&quot;>También registra 21 órdenes de captura vigentes, pero no ha sido trasladada al depósito.</p><div id=&quot;ads_d_inline&quot; style=&quot;color: rgb(51, 51, 51); font-family: Georgia, &quot;Times New Roman&quot;, serif; font-size: 16px;&quot;></div><p class=&quot;parrafo first-parrafo&quot; id=&quot;p531012-m1375-2-1376&quot; style=&quot;font-size: 20px; line-height: 1.5; margin-bottom: 25px; word-wrap: break-word; color: rgb(51, 51, 51); font-family: Georgia, &quot;Times New Roman&quot;, serif;&quot;>Las papeletas que registra este bus, alrededor de 160, fueron emitidas en solo tres años. Es decir, ha generado más de S/60.000 al mes en infracciones. Según el portal de los Registros Públicos, el propietario es Jorge Sócrates Ruiz Ludeña, un hombre de 71 años que vive en Huancavelica. Sin embargo, más de cinco choferes distintos han conducido este vehículo, varios de ellos con un largo historial de multas.</p><p class=&quot;parrafo first-parrafo&quot; id=&quot;p531012-m1385-3-1386&quot; style=&quot;font-size: 20px; line-height: 1.5; margin-bottom: 25px; word-wrap: break-word; color: rgb(51, 51, 51); font-family: Georgia, &quot;Times New Roman&quot;, serif;&quot;>El portal del Ministerio de Transportes (MTC) indica que esta coaster no tiene revisión técnica vehicular vigente ni SOAT.</p><p class=&quot;parrafo first-parrafo&quot; id=&quot;p531012-m1390-4-1391&quot; style=&quot;font-size: 20px; line-height: 1.5; margin-bottom: 25px; word-wrap: break-word; color: rgb(51, 51, 51); font-family: Georgia, &quot;Times New Roman&quot;, serif;&quot;>Hace cerca de un mes,&nbsp;<b>El Comercio</b>&nbsp;reveló que la falta de espacio en los depósitos del SAT y de grúas municipales impiden que cientos de vehículos de transporte público puedan ser internados. La Gerencia de Transporte Urbano (GTU) de la Municipalidad de Lima sostiene que hay varios proyectos para ampliar la infraestructura de depósitos en la ciudad.&nbsp;</p>', '27/ 06/ 2018'),
(3, 3, 'Chofer de combi con 182 mil soles en papeletas atropella a inspectora', 'Jonathan Michael Ramírez Valencia no tenía licencia de conducir y el vehículo que manejaba no contaba con SOAT vigente', '', '<p><span style=&quot;color: rgb(51, 51, 51); font-family: Georgia, &quot;Times New Roman&quot;, serif; font-size: 20px;&quot;>Una inspectora de la Subgerencia de Transito de la Municipalidad de Santiago de </span><b style=&quot;color: rgb(51, 51, 51); font-family: Georgia, &quot;Times New Roman&quot;, serif; font-size: 20px;&quot;><a href=&quot;https://elcomercio.pe/noticias/surco&quot; target=&quot;_blank&quot; style=&quot;color: rgb(51, 51, 51); text-decoration-line: underline;&quot;>Surco</a> </b><span style=&quot;color: rgb(51, 51, 51); font-family: Georgia, &quot;Times New Roman&quot;, serif; font-size: 20px;&quot;>resultó con lesiones luego de ser atropellada por una combi ‘pirata’ en la avenida Tomás Marsano.</span><br></p>', '<p class=&quot;parrafo first-parrafo&quot; id=&quot;p530996-m720-719-721&quot; style=&quot;font-size: 20px; line-height: 1.5; margin-bottom: 25px; word-wrap: break-word; color: rgb(51, 51, 51); font-family: Georgia, &quot;Times New Roman&quot;, serif;&quot;>La agente Ursula Noemí Ninamango Luyo, de 46 años, fue embestida al intentar intervenir a la unidad de placa A0K-740 que dejaba pasajeros en un paradero no autorizado, a la altura de la cuadra 50 de la mencionada vía.</p><p class=&quot;parrafo first-parrafo&quot; id=&quot;p530996-m833-1-834&quot; style=&quot;font-size: 20px; line-height: 1.5; margin-bottom: 25px; word-wrap: break-word; color: rgb(51, 51, 51); font-family: Georgia, &quot;Times New Roman&quot;, serif;&quot;>Tras cometer la agresión, el chofer identificado como Jonathan Michael Ramírez Valencia, de 29 años, se dio a la fuga con pasajeros a bordo, pero fue detenido en la vía auxiliar de la Panamericana Sur y conducido a la comisaría de Sagitario.</p><div id=&quot;ads_d_inline&quot; style=&quot;color: rgb(51, 51, 51); font-family: Georgia, &quot;Times New Roman&quot;, serif; font-size: 16px;&quot;></div><p class=&quot;parrafo first-parrafo&quot; id=&quot;p530996-m843-2-844&quot; style=&quot;font-size: 20px; line-height: 1.5; margin-bottom: 25px; word-wrap: break-word; color: rgb(51, 51, 51); font-family: Georgia, &quot;Times New Roman&quot;, serif;&quot;>Según informó la comuna de Surco, en la sede policial se constató que Ramírez Valencia no tenía licencia de conducir y la combi no contaba con SOAT vigente.</p><p class=&quot;parrafo first-parrafo&quot; id=&quot;p530996-m853-3-854&quot; style=&quot;font-size: 20px; line-height: 1.5; margin-bottom: 25px; word-wrap: break-word; color: rgb(51, 51, 51); font-family: Georgia, &quot;Times New Roman&quot;, serif;&quot;>Además, la unidad ‘pirata’ acumula una deuda de S/181.989,80 mil soles por 29 papeletas de tránsito y transporte, entre graves y muy grave, según consta en el portal del Servicio de Administración Tributaria (SAT). En total, esta combi tiene 16 órdenes de captura.</p><p class=&quot;parrafo first-parrafo&quot; id=&quot;p530996-m858-4-859&quot; style=&quot;font-size: 20px; line-height: 1.5; margin-bottom: 25px; word-wrap: break-word; color: rgb(51, 51, 51); font-family: Georgia, &quot;Times New Roman&quot;, serif;&quot;>La inspectora agraviada fue trasladada al hospital Casimiro Ulloa luego de sufrir golpes en la cabeza y piernas, precisó la comuna.</p>', '20180628123630.jpg', '201806281236302.jpg', '201806281236303.jpg', '', 1, 'Chofer de combi con 182 mil soles en papeletas atropella a inspectora', 'Jonathan Michael Ramírez Valencia no tenía licencia de conducir y el vehículo que manejaba no contaba con SOAT vigente', '', '<p><span style=&quot;color: rgb(51, 51, 51); font-family: Georgia, &quot;Times New Roman&quot;, serif; font-size: 20px;&quot;>Una inspectora de la Subgerencia de Transito de la Municipalidad de Santiago de </span><b style=&quot;color: rgb(51, 51, 51); font-family: Georgia, &quot;Times New Roman&quot;, serif; font-size: 20px;&quot;><a href=&quot;https://elcomercio.pe/noticias/surco&quot; target=&quot;_blank&quot; style=&quot;color: rgb(51, 51, 51); text-decoration-line: underline;&quot;>Surco</a> </b><span style=&quot;color: rgb(51, 51, 51); font-family: Georgia, &quot;Times New Roman&quot;, serif; font-size: 20px;&quot;>resultó con lesiones luego de ser atropellada por una combi ‘pirata’ en la avenida Tomás Marsano.</span><br></p>', '<p class=&quot;parrafo first-parrafo&quot; id=&quot;p530996-m720-719-721&quot; style=&quot;font-size: 20px; line-height: 1.5; margin-bottom: 25px; word-wrap: break-word; color: rgb(51, 51, 51); font-family: Georgia, &quot;Times New Roman&quot;, serif;&quot;>La agente Ursula Noemí Ninamango Luyo, de 46 años, fue embestida al intentar intervenir a la unidad de placa A0K-740 que dejaba pasajeros en un paradero no autorizado, a la altura de la cuadra 50 de la mencionada vía.</p><p class=&quot;parrafo first-parrafo&quot; id=&quot;p530996-m833-1-834&quot; style=&quot;font-size: 20px; line-height: 1.5; margin-bottom: 25px; word-wrap: break-word; color: rgb(51, 51, 51); font-family: Georgia, &quot;Times New Roman&quot;, serif;&quot;>Tras cometer la agresión, el chofer identificado como Jonathan Michael Ramírez Valencia, de 29 años, se dio a la fuga con pasajeros a bordo, pero fue detenido en la vía auxiliar de la Panamericana Sur y conducido a la comisaría de Sagitario.</p><div id=&quot;ads_d_inline&quot; style=&quot;color: rgb(51, 51, 51); font-family: Georgia, &quot;Times New Roman&quot;, serif; font-size: 16px;&quot;></div><p class=&quot;parrafo first-parrafo&quot; id=&quot;p530996-m843-2-844&quot; style=&quot;font-size: 20px; line-height: 1.5; margin-bottom: 25px; word-wrap: break-word; color: rgb(51, 51, 51); font-family: Georgia, &quot;Times New Roman&quot;, serif;&quot;>Según informó la comuna de Surco, en la sede policial se constató que Ramírez Valencia no tenía licencia de conducir y la combi no contaba con SOAT vigente.</p><p class=&quot;parrafo first-parrafo&quot; id=&quot;p530996-m853-3-854&quot; style=&quot;font-size: 20px; line-height: 1.5; margin-bottom: 25px; word-wrap: break-word; color: rgb(51, 51, 51); font-family: Georgia, &quot;Times New Roman&quot;, serif;&quot;>Además, la unidad ‘pirata’ acumula una deuda de S/181.989,80 mil soles por 29 papeletas de tránsito y transporte, entre graves y muy grave, según consta en el portal del Servicio de Administración Tributaria (SAT). En total, esta combi tiene 16 órdenes de captura.</p><p class=&quot;parrafo first-parrafo&quot; id=&quot;p530996-m858-4-859&quot; style=&quot;font-size: 20px; line-height: 1.5; margin-bottom: 25px; word-wrap: break-word; color: rgb(51, 51, 51); font-family: Georgia, &quot;Times New Roman&quot;, serif;&quot;>La inspectora agraviada fue trasladada al hospital Casimiro Ulloa luego de sufrir golpes en la cabeza y piernas, precisó la comuna.</p>', '26/ 06/ 2018'),
(4, 4, 'Línea Amarilla: aún no liberan vías del servicio de buses', 'MML no cumple con sacar a coasters, combis y otras unidades de la ruta de buses amarillos. Pro Transporte dice que retiro acabará en la próxima gestión', '', '<p><span style=&quot;color: rgb(51, 51, 51); font-family: Georgia, &quot;Times New Roman&quot;, serif; font-size: 20px;&quot;>Juan Ocampo llega a Puente Nuevo, en&nbsp;</span><a href=&quot;http://elcomercio.pe/noticias/el-agustino&quot; style=&quot;color: rgb(51, 51, 51); text-decoration-line: underline; font-family: Georgia, &quot;Times New Roman&quot;, serif; font-size: 20px; background-color: rgb(255, 255, 255);&quot;><b>El Agustino</b></a><span style=&quot;color: rgb(51, 51, 51); font-family: Georgia, &quot;Times New Roman&quot;, serif; font-size: 20px;&quot;>, y mira con curiosidad un ómnibus amarillo con 15 pasajeros. No lo ha visto antes. Un inspector municipal le entrega al universitario de 22 años un volante con la ruta del vehículo y los paraderos. Juan sube y paga S/2,50 al chofer. Se dirige hacia el límite de Los Olivos con San Martín de Porres (SMP), distrito donde terminará su recorrido la unidad.</span><br></p>', '<p class=&quot;parrafo first-parrafo&quot; id=&quot;p530458-m778-2-779&quot; style=&quot;font-size: 20px; line-height: 1.5; margin-bottom: 25px; word-wrap: break-word; color: rgb(51, 51, 51); font-family: Georgia, &quot;Times New Roman&quot;, serif;&quot;>Este vehículo es uno de los 53 buses del corredor complementario Panamericana que se implementó hace dos semanas en la Vía de Evitamiento. El servicio 107 va desde el trébol de Javier Prado, en Surco, hasta la avenida Canta Callao, en SMP, pasando por un túnel de dos kilómetros debajo del río Rímac. La ruta que recorren estos vehículos ha sido bautizada por el alcalde Luis Castañeda como el “corredor amarillo” debido a que está dentro de la<b>&nbsp;<a href=&quot;https://elcomercio.pe/noticias/linea-amarilla&quot; style=&quot;color: rgb(51, 51, 51); text-decoration-line: underline;&quot;>vía expresa Línea Amarilla</a></b>, inaugurada, luego de muchas idas y vueltas, el pasado 15 de junio.</p><div id=&quot;ads_d_inline&quot; style=&quot;color: rgb(51, 51, 51); font-family: Georgia, &quot;Times New Roman&quot;, serif; font-size: 16px;&quot;></div><p class=&quot;parrafo first-parrafo&quot; id=&quot;p530458-m788-3-789&quot; style=&quot;font-size: 20px; line-height: 1.5; margin-bottom: 25px; word-wrap: break-word; color: rgb(51, 51, 51); font-family: Georgia, &quot;Times New Roman&quot;, serif;&quot;>Después de 42 minutos, Juan llega a su destino. Antes tardaba una hora y media. Aunque el nuevo corredor busca ser una alternativa para trasladar, en su primera etapa, a 33 mil pasajeros al día, este servicio aún no se encuentra implementado en su totalidad.</p><p class=&quot;parrafo first-parrafo&quot; id=&quot;p530458-m793-4-794&quot; style=&quot;font-size: 20px; line-height: 1.5; margin-bottom: 25px; word-wrap: break-word; color: rgb(51, 51, 51); font-family: Georgia, &quot;Times New Roman&quot;, serif;&quot;>Polo Pérez, presidente de la empresa Perú Bus Internacional, operadora del servicio del corredor, sostiene que la Municipalidad de Lima no ha cumplido con el retiro de combis, coasters y otros vehículos de transporte público que transitan por la ruta del nuevo servicio.</p><p class=&quot;parrafo first-parrafo&quot; id=&quot;p530458-m798-5-799&quot; style=&quot;font-size: 20px; line-height: 1.5; margin-bottom: 25px; word-wrap: break-word; color: rgb(51, 51, 51); font-family: Georgia, &quot;Times New Roman&quot;, serif;&quot;>Además, según el contrato de la licitación, los buses del corredor debían transitar por la Panamericana Sur, Evitamiento y la Panamericana Norte. Sin embargo, la comuna ha determinado que los buses amarillos avancen por el túnel, pese a que eso no indicaba el documento.</p><p class=&quot;parrafo first-parrafo&quot; id=&quot;p530458-m803-6-804&quot; style=&quot;font-size: 20px; line-height: 1.5; margin-bottom: 25px; word-wrap: break-word; color: rgb(51, 51, 51); font-family: Georgia, &quot;Times New Roman&quot;, serif;&quot;>Mientras tanto, la congestión en las vías ubicadas encima del túnel persiste, sobre todo en horas punta.</p>', '20180628124015.jpg', '201806281240152.jpg', '', '', 1, 'Línea Amarilla: aún no liberan vías del servicio de buses', 'MML no cumple con sacar a coasters, combis y otras unidades de la ruta de buses amarillos. Pro Transporte dice que retiro acabará en la próxima gestión', '', '<p><span style=&quot;color: rgb(51, 51, 51); font-family: Georgia, &quot;Times New Roman&quot;, serif; font-size: 20px;&quot;>Juan Ocampo llega a Puente Nuevo, en&nbsp;</span><a href=&quot;http://elcomercio.pe/noticias/el-agustino&quot; style=&quot;color: rgb(51, 51, 51); text-decoration-line: underline; font-family: Georgia, &quot;Times New Roman&quot;, serif; font-size: 20px; background-color: rgb(255, 255, 255);&quot;><b>El Agustino</b></a><span style=&quot;color: rgb(51, 51, 51); font-family: Georgia, &quot;Times New Roman&quot;, serif; font-size: 20px;&quot;>, y mira con curiosidad un ómnibus amarillo con 15 pasajeros. No lo ha visto antes. Un inspector municipal le entrega al universitario de 22 años un volante con la ruta del vehículo y los paraderos. Juan sube y paga S/2,50 al chofer. Se dirige hacia el límite de Los Olivos con San Martín de Porres (SMP), distrito donde terminará su recorrido la unidad.</span><br></p>', '<p class=&quot;parrafo first-parrafo&quot; id=&quot;p530458-m778-2-779&quot; style=&quot;font-size: 20px; line-height: 1.5; margin-bottom: 25px; word-wrap: break-word; color: rgb(51, 51, 51); font-family: Georgia, &quot;Times New Roman&quot;, serif;&quot;>Este vehículo es uno de los 53 buses del corredor complementario Panamericana que se implementó hace dos semanas en la Vía de Evitamiento. El servicio 107 va desde el trébol de Javier Prado, en Surco, hasta la avenida Canta Callao, en SMP, pasando por un túnel de dos kilómetros debajo del río Rímac. La ruta que recorren estos vehículos ha sido bautizada por el alcalde Luis Castañeda como el “corredor amarillo” debido a que está dentro de la<b>&nbsp;<a href=&quot;https://elcomercio.pe/noticias/linea-amarilla&quot; style=&quot;color: rgb(51, 51, 51); text-decoration-line: underline;&quot;>vía expresa Línea Amarilla</a></b>, inaugurada, luego de muchas idas y vueltas, el pasado 15 de junio.</p><div id=&quot;ads_d_inline&quot; style=&quot;color: rgb(51, 51, 51); font-family: Georgia, &quot;Times New Roman&quot;, serif; font-size: 16px;&quot;></div><p class=&quot;parrafo first-parrafo&quot; id=&quot;p530458-m788-3-789&quot; style=&quot;font-size: 20px; line-height: 1.5; margin-bottom: 25px; word-wrap: break-word; color: rgb(51, 51, 51); font-family: Georgia, &quot;Times New Roman&quot;, serif;&quot;>Después de 42 minutos, Juan llega a su destino. Antes tardaba una hora y media. Aunque el nuevo corredor busca ser una alternativa para trasladar, en su primera etapa, a 33 mil pasajeros al día, este servicio aún no se encuentra implementado en su totalidad.</p><p class=&quot;parrafo first-parrafo&quot; id=&quot;p530458-m793-4-794&quot; style=&quot;font-size: 20px; line-height: 1.5; margin-bottom: 25px; word-wrap: break-word; color: rgb(51, 51, 51); font-family: Georgia, &quot;Times New Roman&quot;, serif;&quot;>Polo Pérez, presidente de la empresa Perú Bus Internacional, operadora del servicio del corredor, sostiene que la Municipalidad de Lima no ha cumplido con el retiro de combis, coasters y otros vehículos de transporte público que transitan por la ruta del nuevo servicio.</p><p class=&quot;parrafo first-parrafo&quot; id=&quot;p530458-m798-5-799&quot; style=&quot;font-size: 20px; line-height: 1.5; margin-bottom: 25px; word-wrap: break-word; color: rgb(51, 51, 51); font-family: Georgia, &quot;Times New Roman&quot;, serif;&quot;>Además, según el contrato de la licitación, los buses del corredor debían transitar por la Panamericana Sur, Evitamiento y la Panamericana Norte. Sin embargo, la comuna ha determinado que los buses amarillos avancen por el túnel, pese a que eso no indicaba el documento.</p><p class=&quot;parrafo first-parrafo&quot; id=&quot;p530458-m803-6-804&quot; style=&quot;font-size: 20px; line-height: 1.5; margin-bottom: 25px; word-wrap: break-word; color: rgb(51, 51, 51); font-family: Georgia, &quot;Times New Roman&quot;, serif;&quot;>Mientras tanto, la congestión en las vías ubicadas encima del túnel persiste, sobre todo en horas punta.</p>', '25/ 06/ 2018'),
(5, 5, 'Quienes deseen continuar utilizando el servicio deberán solicitar un pase especial al personal en la', 'Con motivo de las obras de ampliación de la Línea 1 del Metro de Lima, este domingo 24 de junio desde las 6 de la mañana cerrarán dos estaciones hasta el mediodía.', '', '<p><span style=&quot;color: rgb(51, 51, 51); font-family: Georgia, &quot;Times New Roman&quot;, serif; font-size: 20px;&quot;>Con motivo de las obras de ampliación de la Línea 1 del&nbsp;</span><b style=&quot;color: rgb(51, 51, 51); font-family: Georgia, &quot;Times New Roman&quot;, serif; font-size: 20px;&quot;><a href=&quot;https://elcomercio.pe/noticias/metro-de-lima&quot; target=&quot;_blank&quot; style=&quot;color: rgb(51, 51, 51); text-decoration-line: underline;&quot;>Metro de Lima</a></b><span style=&quot;color: rgb(51, 51, 51); font-family: Georgia, &quot;Times New Roman&quot;, serif; font-size: 20px;&quot;>, este domingo 24 de junio desde las 6 de la mañana cerrarán dos estaciones hasta el mediodía.</span><br></p>', '<p class=&quot;parrafo first-parrafo&quot; id=&quot;p530102-m139-138-140&quot; style=&quot;font-size: 20px; line-height: 1.5; margin-bottom: 25px; word-wrap: break-word; color: rgb(51, 51, 51); font-family: Georgia, &quot;Times New Roman&quot;, serif;&quot;>Se trata de las estaciones&nbsp;<b>Nicolás Arriola y Gamarra</b>&nbsp;que permanecerán cerradas al público durante seis horas. Por esta razón, el servicio se brindará en dos zonas:</p><p class=&quot;parrafo first-parrafo&quot; id=&quot;p530102-m193-1-194&quot; style=&quot;font-size: 20px; line-height: 1.5; margin-bottom: 25px; word-wrap: break-word; color: rgb(51, 51, 51); font-family: Georgia, &quot;Times New Roman&quot;, serif;&quot;>-	Zona 1: Desde la Estación Villa el Salvador hasta la Estación La Cultura con trenes cada 9,30 minutos (tiempo referencial).&nbsp;<br>-	Zona 2: Desde la Estación Miguel Grau hasta la Estación Bayóvar.</p><div id=&quot;ads_d_inline&quot; style=&quot;color: rgb(51, 51, 51); font-family: Georgia, &quot;Times New Roman&quot;, serif; font-size: 16px;&quot;></div><p class=&quot;parrafo first-parrafo&quot; id=&quot;p530102-m203-2-204&quot; style=&quot;font-size: 20px; line-height: 1.5; margin-bottom: 25px; word-wrap: break-word; color: rgb(51, 51, 51); font-family: Georgia, &quot;Times New Roman&quot;, serif;&quot;>(a) Servicio desde Miguel Grau hasta Los Jardines con trenes cada 30 minutos. (Tiempo referencial).<br>(b) Servicio regular desde Jardines hasta Bayóvar con trenes cada 10 minutos. (Tiempo referencial).</p><p class=&quot;parrafo first-parrafo&quot; id=&quot;p530102-m213-3-214&quot; style=&quot;font-size: 20px; line-height: 1.5; margin-bottom: 25px; word-wrap: break-word; color: rgb(51, 51, 51); font-family: Georgia, &quot;Times New Roman&quot;, serif;&quot;>Quienes deseen continuar utilizando el servicio deberán solicitar un pase especial al personal de la Línea 1 del Metro de Lima en las estaciones La Cultura y Miguel Grau para evitar el recobro de pasaje.</p>', '20180628124349.jpg', '', '', '', 1, 'Quienes deseen continuar utilizando el servicio deberán solicitar un pase especial al personal en la', 'Con motivo de las obras de ampliación de la Línea 1 del Metro de Lima, este domingo 24 de junio desde las 6 de la mañana cerrarán dos estaciones hasta el mediodía.', '', '<p><span style=&quot;color: rgb(51, 51, 51); font-family: Georgia, &quot;Times New Roman&quot;, serif; font-size: 20px;&quot;>Con motivo de las obras de ampliación de la Línea 1 del&nbsp;</span><b style=&quot;color: rgb(51, 51, 51); font-family: Georgia, &quot;Times New Roman&quot;, serif; font-size: 20px;&quot;><a href=&quot;https://elcomercio.pe/noticias/metro-de-lima&quot; target=&quot;_blank&quot; style=&quot;color: rgb(51, 51, 51); text-decoration-line: underline;&quot;>Metro de Lima</a></b><span style=&quot;color: rgb(51, 51, 51); font-family: Georgia, &quot;Times New Roman&quot;, serif; font-size: 20px;&quot;>, este domingo 24 de junio desde las 6 de la mañana cerrarán dos estaciones hasta el mediodía.</span><br></p>', '<p class=&quot;parrafo first-parrafo&quot; id=&quot;p530102-m139-138-140&quot; style=&quot;font-size: 20px; line-height: 1.5; margin-bottom: 25px; word-wrap: break-word; color: rgb(51, 51, 51); font-family: Georgia, &quot;Times New Roman&quot;, serif;&quot;>Se trata de las estaciones&nbsp;<b>Nicolás Arriola y Gamarra</b>&nbsp;que permanecerán cerradas al público durante seis horas. Por esta razón, el servicio se brindará en dos zonas:</p><p class=&quot;parrafo first-parrafo&quot; id=&quot;p530102-m193-1-194&quot; style=&quot;font-size: 20px; line-height: 1.5; margin-bottom: 25px; word-wrap: break-word; color: rgb(51, 51, 51); font-family: Georgia, &quot;Times New Roman&quot;, serif;&quot;>-	Zona 1: Desde la Estación Villa el Salvador hasta la Estación La Cultura con trenes cada 9,30 minutos (tiempo referencial).&nbsp;<br>-	Zona 2: Desde la Estación Miguel Grau hasta la Estación Bayóvar.</p><div id=&quot;ads_d_inline&quot; style=&quot;color: rgb(51, 51, 51); font-family: Georgia, &quot;Times New Roman&quot;, serif; font-size: 16px;&quot;></div><p class=&quot;parrafo first-parrafo&quot; id=&quot;p530102-m203-2-204&quot; style=&quot;font-size: 20px; line-height: 1.5; margin-bottom: 25px; word-wrap: break-word; color: rgb(51, 51, 51); font-family: Georgia, &quot;Times New Roman&quot;, serif;&quot;>(a) Servicio desde Miguel Grau hasta Los Jardines con trenes cada 30 minutos. (Tiempo referencial).<br>(b) Servicio regular desde Jardines hasta Bayóvar con trenes cada 10 minutos. (Tiempo referencial).</p><p class=&quot;parrafo first-parrafo&quot; id=&quot;p530102-m213-3-214&quot; style=&quot;font-size: 20px; line-height: 1.5; margin-bottom: 25px; word-wrap: break-word; color: rgb(51, 51, 51); font-family: Georgia, &quot;Times New Roman&quot;, serif;&quot;>Quienes deseen continuar utilizando el servicio deberán solicitar un pase especial al personal de la Línea 1 del Metro de Lima en las estaciones La Cultura y Miguel Grau para evitar el recobro de pasaje.</p>', '24/ 06/ 2018');
INSERT INTO `noticia` (`noticia_id`, `noticia_orden`, `noticia_titulo`, `noticia_subtitulo`, `noticia_descripcion`, `noticia_descripcion2`, `noticia_descripcion3`, `noticia_imagen`, `noticia_imagen2`, `noticia_imagen3`, `noticia_link_face`, `noticia_activo`, `noticia_titulo_ingles`, `noticia_subtitulo_ingles`, `noticia_descripcion_ingles`, `noticia_descripcion2_ingles`, `noticia_descripcion3_ingles`, `noticia_fecha`) VALUES
(6, 6, 'Caos en el Metropolitano por mala programación de buses', 'Pasajeros reclaman por colas y falta de buses en horas punta. Según operadores, fallas en el servicio se deben a incumplimientos contractuales de la MML. Especialista propone actualizar estudios de demanda', '', '<p><span style=&quot;color: rgb(51, 51, 51); font-family: Georgia, &quot;Times New Roman&quot;, serif; font-size: 20px;&quot;>‘Espacio’: según la RAE, es la “extensión que contiene toda la materia existente” o la “distancia entre dos cuerpos”. Para los limeños que utilizan a diario el Metropolitano</span><span style=&quot;color: rgb(51, 51, 51); font-family: Georgia, &quot;Times New Roman&quot;, serif; font-size: 20px;&quot;>, podría existir otra acepción: espacio es lo que nunca se encuentra en los buses o las estaciones.</span><br></p>', '<p><span style=&quot;color: rgb(51, 51, 51); font-family: Georgia, &quot;Times New Roman&quot;, serif; font-size: 20px;&quot;>Marina es una usuaria que todos los días lucha para no desaparecer entre tanto cuerpo apretujado. A las 6 p.m., en la estación central, corretea de un lado a otro en busca de un bus que la lleve a Chorrillos.</span></p><p class=&quot;parrafo first-parrafo &quot; id=&quot;p530094-m2189-2-2190&quot; style=&quot;font-size: 20px; line-height: 1.5; margin-bottom: 25px; word-wrap: break-word; color: rgb(51, 51, 51); font-family: Georgia, &quot;Times New Roman&quot;, serif;&quot;>“A veces estoy esperando varios minutos a que pase una C, pero se demoran o están llenas. Cuando veo que en otro embarque hay un bus de otra línea que también va al sur, dejo mi cola y corro para alcanzarlo”, dice.</p><div id=&quot;ads_d_inline&quot; style=&quot;color: rgb(51, 51, 51); font-family: Georgia, &quot;Times New Roman&quot;, serif; font-size: 16px;&quot;></div><p class=&quot;parrafo first-parrafo &quot; id=&quot;p530094-m2199-3-2200&quot; style=&quot;font-size: 20px; line-height: 1.5; margin-bottom: 25px; word-wrap: break-word; color: rgb(51, 51, 51); font-family: Georgia, &quot;Times New Roman&quot;, serif;&quot;>Según la joven, no se puede predecir en qué cola encontrará sitio porque hay días en que puede haber muchas C y otros en los que escasean. Esta incertidumbre surge desde que se ingresa a las estaciones, donde muchas pantallas digitales no funcionan o dan información errada. Se puede leer que alguna línea llegará en tres minutos, pero bien podrían pasar diez sin que aparezca el bus. Lejos está este servicio de lo que sucede en ciudades como Madrid, donde uno puede saber el minuto exacto de llegada de un bus a un paradero a través de una aplicación.</p><p class=&quot;parrafo first-parrafo &quot; id=&quot;p530094-m2231-5-2232&quot; style=&quot;font-size: 20px; line-height: 1.5; margin-bottom: 25px; word-wrap: break-word; color: rgb(51, 51, 51); font-family: Georgia, &quot;Times New Roman&quot;, serif;&quot;>Además de la saturación del servicio (los pasajeros pueden esperar hasta 40 minutos en colas que invaden varias puertas de embarque), El Comercio constató junto a especialistas que el Metropolitano tiene problemas de sincronización de buses. Por ejemplo, todos los días, desde las 7 p.m., por cada tres buses de la línea A que circulan hacia el Centro de Lima solo sale una línea C, que se dirige al sur. Decenas esperan entre 15 minutos y media hora hasta que aparecen estas unidades.</p><p class=&quot;parrafo first-parrafo &quot; id=&quot;p530094-m2236-6-2237&quot; style=&quot;font-size: 20px; line-height: 1.5; margin-bottom: 25px; word-wrap: break-word; color: rgb(51, 51, 51); font-family: Georgia, &quot;Times New Roman&quot;, serif;&quot;>Esto se repite en todas las estaciones. Cerca de las 3 p.m. de ayer, pasaron tres buses de la línea B seguidos por la estación 28 de Julio (los dos últimos casi vacíos) y recién después de varios minutos apareció uno repleto de la línea C.</p><p class=&quot;parrafo first-parrafo &quot; id=&quot;p530094-m2241-7-2242&quot; style=&quot;font-size: 20px; line-height: 1.5; margin-bottom: 25px; word-wrap: break-word; color: rgb(51, 51, 51); font-family: Georgia, &quot;Times New Roman&quot;, serif;&quot;>Los problemas de programación incluso generan que los pasajeros se queden encerrados en las estaciones. El lunes pasado, un grupo de personas que recién habían descendido de los buses no podía salir de la estación Javier Prado. Según el controlador, existía la orden de no abrir las puertas a partir de las 11:16 p.m. Sin embargo, pasadas las 11:30 p.m., los buses seguían dejando pasajeros en ese punto. Luego de 15 minutos, los dejaron salir. A las 11:40 p.m. otro grupo llegó y estuvo atrapado varios minutos. Ningún operario los atendió, así que forzaron la puerta para salir.</p><p class=&quot;parrafo first-parrafo &quot; id=&quot;p530094-m2246-8-2247&quot; style=&quot;font-size: 20px; line-height: 1.5; margin-bottom: 25px; word-wrap: break-word; color: rgb(51, 51, 51); font-family: Georgia, &quot;Times New Roman&quot;, serif;&quot;>¿Por qué sucede todo esto?</p><p class=&quot;parrafo first-parrafo &quot; id=&quot;p530094-m2251-9-2252&quot; style=&quot;font-size: 20px; line-height: 1.5; margin-bottom: 25px; word-wrap: break-word; color: rgb(51, 51, 51); font-family: Georgia, &quot;Times New Roman&quot;, serif;&quot;><i>—Acusaciones mutuas—<br></i>Pro Transporte, entidad de la Municipalidad de Lima que administra y controla el servicio (y, por ende, sincroniza los buses), asegura que cuentan con un sistema de control y que una de sus medidas es enviar más expresos a estaciones como Canaval y Moreyra, Javier Prado o Canadá.</p><p class=&quot;parrafo first-parrafo &quot; id=&quot;p530094-m2256-10-2257&quot; style=&quot;font-size: 20px; line-height: 1.5; margin-bottom: 25px; word-wrap: break-word; color: rgb(51, 51, 51); font-family: Georgia, &quot;Times New Roman&quot;, serif;&quot;>Según Pro Transporte, a diario se realizan 700 mil viajes en las rutas troncales y alimentadoras (el doble del 2010, cuando empezó a operar el servicio). De acuerdo con el contrato, al llegar a esta cifra los operadores deberían incrementar el número de buses, según esta entidad. “Ahora operamos con la cantidad máxima de flota [...]: 300 buses articulados en la troncal y 222 buses alimentadores”, anota un comunicado enviado a este Diario.</p><p class=&quot;parrafo first-parrafo &quot; id=&quot;p530094-m2261-11-2262&quot; style=&quot;font-size: 20px; line-height: 1.5; margin-bottom: 25px; word-wrap: break-word; color: rgb(51, 51, 51); font-family: Georgia, &quot;Times New Roman&quot;, serif;&quot;>Los operadores tienen otra versión. Ellos aseguran que a diario solo mueven a 500 mil pasajeros. Afirman que en los cálculos de la comuna no se toma en cuenta a los usuarios sino las validaciones, es decir el paso de la tarjeta por el lector, por lo que una persona podría estar haciendo más de un viaje.</p><p class=&quot;parrafo first-parrafo &quot; id=&quot;p530094-m2266-12-2267&quot; style=&quot;font-size: 20px; line-height: 1.5; margin-bottom: 25px; word-wrap: break-word; color: rgb(51, 51, 51); font-family: Georgia, &quot;Times New Roman&quot;, serif;&quot;>Añaden que no pueden incrementar la flota porque la actual infraestructura está incompleta. “La municipalidad no ha cumplido sus obligaciones contractuales: erradicar los buses informales a lo largo del trayecto y completar la vía troncal hacia Carabayllo. Si ponemos más buses en estas condiciones, la congestión en las estaciones podría incrementarse”, explica Natale Amprimo, asesor legal de Transvial, Lima Vías Express y Perú Masivo.</p><p class=&quot;parrafo first-parrafo &quot; id=&quot;p530094-m2271-13-2272&quot; style=&quot;font-size: 20px; line-height: 1.5; margin-bottom: 25px; word-wrap: break-word; color: rgb(51, 51, 51); font-family: Georgia, &quot;Times New Roman&quot;, serif;&quot;>Estos incumplimientos, según Amprimo, generan fallas en la programación de las rutas y afectan la fluidez de los buses.</p><p class=&quot;parrafo first-parrafo &quot; id=&quot;p530094-m2276-14-2277&quot; style=&quot;font-size: 20px; line-height: 1.5; margin-bottom: 25px; word-wrap: break-word; color: rgb(51, 51, 51); font-family: Georgia, &quot;Times New Roman&quot;, serif;&quot;><i>—¿Qué hacer?—<br></i>Alexandre Almeida, director de Ingeniería Civil de la Universidad de Lima, señala que el modelo de programación debería priorizar el bienestar del usuario. “No solo se necesita que pasen más buses sino que tengan capacidad para recoger a los pasajeros sin que viajen apiñados”.</p><p class=&quot;parrafo first-parrafo &quot; id=&quot;p530094-m2281-15-2282&quot; style=&quot;font-size: 20px; line-height: 1.5; margin-bottom: 25px; word-wrap: break-word; color: rgb(51, 51, 51); font-family: Georgia, &quot;Times New Roman&quot;, serif;&quot;>Indica que se deben actualizar los estudios de demanda del Metropolitano para atender bien a los usuarios. “El tráfico es cambiante porque una ciudad es dinámica. Al menos cada año debe ajustarse el modelo para saber en qué estaciones y horarios se requiere más buses”, dice.</p><p class=&quot;parrafo first-parrafo &quot; id=&quot;p530094-m2286-16-2287&quot; style=&quot;font-size: 20px; line-height: 1.5; margin-bottom: 25px; word-wrap: break-word; color: rgb(51, 51, 51); font-family: Georgia, &quot;Times New Roman&quot;, serif;&quot;>Luis Quispe Candia, presidente de la ONG Luz Ámbar, recomienda a la comuna tercerizar el control y programación del servicio a una empresa especializada. De esta forma se podrían poner parámetros y penalidades en caso de que se incumpla la sincronización de rutas. ￼</p><div class=&quot;embedded-module &quot; id=&quot;p530094-m2291-17-2292&quot; style=&quot;border-width: 1px 0px; border-style: solid; border-color: rgb(153, 153, 153); margin-bottom: 30px; padding: 20px 0px; font-family: &quot;Open Sans&quot;; color: rgb(153, 153, 153); font-size: 16px;&quot;><span id=&quot;m2295-2294-2296&quot; class=&quot;ficha2 &quot;><b>DATOS</b></span><p id=&quot;m2300-1-2301&quot; class=&quot;ficha xx &quot; style=&quot;font-size: 15px; line-height: 1.6; margin-bottom: 0px;&quot;>- 37 estaciones tiene la ruta troncal del Metropolitano que va desde Chorrillos hasta Independencia. Entre servicios regulares y expresos tiene 14 rutas.&nbsp;<br>- S/9 millones invertirá la Municipalidad de Lima en construir la nueva estación Andrés Reyes, en San Isidro. Estará lista en octubre, según Emape.<br>- 2,5 soles vale el pasaje del Metropolitano. Este precio se mantiene desde el 2014. En el 2010, el precio inicial fue de S/1,5. En el 2012, subió a S/2.</p></div>', '20180628125708.jpg', '201806281257082.jpg', '201806281257083.jpg', '', 1, 'Caos en el Metropolitano por mala programación de buses', 'Pasajeros reclaman por colas y falta de buses en horas punta. Según operadores, fallas en el servicio se deben a incumplimientos contractuales de la MML. Especialista propone actualizar estudios de demanda', '', '<p><span style=&quot;color: rgb(51, 51, 51); font-family: Georgia, &quot;Times New Roman&quot;, serif; font-size: 20px;&quot;>‘Espacio’: según la RAE, es la “extensión que contiene toda la materia existente” o la “distancia entre dos cuerpos”. Para los limeños que utilizan a diario el</span><span style=&quot;color: rgb(51, 51, 51); font-family: Georgia, &quot;Times New Roman&quot;, serif; font-size: 20px;&quot;>, Metropolitano podría existir otra acepción: espacio es lo que nunca se encuentra en los buses o las estaciones.</span><br></p>', '<p><span style=&quot;color: rgb(51, 51, 51); font-family: Georgia, &quot;Times New Roman&quot;, serif; font-size: 20px;&quot;>Marina es una usuaria que todos los días lucha para no desaparecer entre tanto cuerpo apretujado. A las 6 p.m., en la estación central, corretea de un lado a otro en busca de un bus que la lleve a Chorrillos.</span></p><p class=&quot;parrafo first-parrafo &quot; id=&quot;p530094-m2189-2-2190&quot; style=&quot;font-size: 20px; line-height: 1.5; margin-bottom: 25px; word-wrap: break-word; color: rgb(51, 51, 51); font-fam